u0432\\u0430\\u043d\\u0438\\u0435\\r\\n.IsDependentOn(&quot;B&quot;)\\r\\n.Does(() =&gt;\\r\\n{\\r\\n    \\/\\/\\u0420\\u0435\\u0430\\u043b\\u0438\\u0437\\u0430\\u0446\\u0438\\u044f Task A\\r\\n});<\\/code><\\/pre>\\r\\n\\r\\n<p>&nbsp;<\\/p>\\r\\n\\r\\n<p>\\u0422\\u0430\\u043a\\u0436\\u0435 \\u043c\\u043e\\u0436\\u043d\\u043e \\u043b\\u0435\\u0433\\u043a\\u043e \\u0437\\u0430\\u0434\\u0430\\u0432\\u0430\\u0442\\u044c \\u0443\\u0441\\u043b\\u043e\\u0432\\u0438\\u044f, \\u043f\\u0440\\u0438 \\u043a\\u043e\\u0442\\u043e\\u0440\\u044b\\u0445 \\u0437\\u0430\\u0434\\u0430\\u0447\\u0430 \\u0431\\u0443\\u0434\\u0435\\u0442 \\u0438\\u043b\\u0438 \\u043d\\u0435 \\u0431\\u0443\\u0434\\u0435\\u0442 \\u0432\\u044b\\u043f\\u043e\\u043b\\u043d\\u044f\\u0442\\u044c\\u0441\\u044f \\u0441 \\u043f\\u043e\\u043c\\u043e\\u0449\\u044c\\u044e \\u043c\\u0435\\u0442\\u043e\\u0434\\u0430&nbsp;<em>WithCriteria<\\/em>:<\\/p>\\r\\n\\r\\n<p>&nbsp;<\\/p>\\r\\n\\r\\n<pre>\\r\\n<code>Task(&quot;B&quot;) \\/\\/ \\u041d\\u0430\\u0437\\u0432\\u0430\\u043d\\u0438\\u0435\\r\\n.IsDependentOn(&quot;C&quot;)\\r\\n.WithCriteria(DateTime.Now.Second % 2 == 0)\\r\\n.Does(() =&gt;\\r\\n{\\r\\n    \\/\\/\\u0420\\u0435\\u0430\\u043b\\u0438\\u0437\\u0430\\u0446\\u0438\\u044f Task A\\r\\n});<\\/code><\\/pre>\\r\\n\\r\\n<p>&nbsp;<\\/p>\\r\\n\\r\\n<p>\\u0415\\u0441\\u043b\\u0438 \\u0436\\u0435 \\u043a\\u0430\\u043a\\u0430\\u044f-\\u0442\\u043e \\u0437\\u0430\\u0434\\u0430\\u0447\\u0430 \\u0437\\u0430\\u0432\\u0438\\u0441\\u0438\\u0442 \\u043e\\u0442 \\u0437\\u0430\\u0434\\u0430\\u0447\\u0438 B, \\u0430 \\u043a\\u0440\\u0438\\u0442\\u0435\\u0440\\u0438\\u0439 \\u043f\\u0440\\u0438\\u043d\\u0438\\u043c\\u0430\\u0435\\u0442 \\u0437\\u043d\\u0430\\u0447\\u0435\\u043d\\u0438\\u0435 false, \\u0442\\u043e \\u0437\\u0430\\u0434\\u0430\\u0447\\u0430 B \\u043d\\u0435 \\u0432\\u044b\\u043f\\u043e\\u043b\\u043d\\u0438\\u0442\\u0441\\u044f, \\u043e\\u0434\\u043d\\u0430\\u043a\\u043e \\u043f\\u043e\\u0442\\u043e\\u043a \\u0438\\u0