0432\\u043b\\u044f\\u0435\\u043c\\u044b\\u0435 \\u044f\\u0434\\u0440\\u043e\\u043c \\u041e\\u0421 \\u0438\\u043d\\u0441\\u0442\\u0440\\u0443\\u043c\\u0435\\u043d\\u0442\\u044b \\u0434\\u043b\\u044f \\u0440\\u0430\\u0431\\u043e\\u0442\\u044b \\u0441 \\u0431\\u043e\\u043b\\u044c\\u0448\\u0438\\u043c \\u043e\\u0431\\u044a\\u0451\\u043c\\u043e\\u043c \\u0432\\u0432\\u043e\\u0434\\u0430\\/\\u0432\\u044b\\u0432\\u043e\\u0434\\u0430 (epoll \\u0438 \\u043f\\u0440.). \\u0421\\u0438\\u0442\\u0443\\u0430\\u0446\\u0438\\u044e \\u0443\\u0441\\u0443\\u0433\\u0443\\u0431\\u043b\\u044f\\u0435\\u0442 \\u0442\\u043e, \\u0447\\u0442\\u043e \\u0432\\u044b\\u043f\\u043e\\u043b\\u043d\\u0435\\u043d\\u0438\\u0435 \\u043e\\u0442\\u0434\\u0435\\u043b\\u044c\\u043d\\u043e\\u0433\\u043e \\u043f\\u0440\\u043e\\u0446\\u0435\\u0441\\u0441\\u0430 \\u043d\\u0430 \\u043a\\u0430\\u0436\\u0434\\u044b\\u0439 \\u0437\\u0430\\u043f\\u0440\\u043e\\u0441 \\u043f\\u0440\\u0438\\u0432\\u043e\\u0434\\u0438\\u0442 \\u043a \\u043f\\u043e\\u0442\\u0440\\u0435\\u0431\\u043b\\u0435\\u043d\\u0438\\u044e \\u0431\\u043e\\u043b\\u044c\\u0448\\u043e\\u0433\\u043e \\u043e\\u0431\\u044a\\u0451\\u043c\\u0430 \\u0441\\u0438\\u0441\\u0442\\u0435\\u043c\\u043d\\u044b\\u0445 \\u0440\\u0435\\u0441\\u0443\\u0440\\u0441\\u043e\\u0432, \\u043e\\u0441\\u043e\\u0431\\u0435\\u043d\\u043d\\u043e \\u043f\\u0430\\u043c\\u044f\\u0442\\u0438, \\u043a\\u043e\\u0442\\u043e\\u0440\\u0430\\u044f \\u0437\\u0430\\u0447\\u0430\\u0441\\u0442\\u0443\\u044e \\u0437\\u0430\\u043a\\u0430\\u043d\\u0447\\u0438\\u0432\\u0430\\u0435\\u0442\\u0441\\u044f \\u0432 \\u043f\\u0435\\u0440\\u0432\\u0443\\u044e \\u043e\\u0447\\u0435\\u0440\\u0435\\u0434\\u044c.<\\/p>\\r\\n\\r\\n<p>&nbsp;<\\/p>\\r\\n\\r\\n<p><em>\\u041f\\u0440\\u0438\\u043c\\u0435\\u0447\\u0430\\u043d\\u0438\\u0435: \\u0432 Ruby \\u0438\\u0441\\u043f\\u043e\\u043b\\u044c\\u0437\\u0443\\u0435\\u0442\\u0441\\u044f \\u043e\\u0447\\u0435\\u043d\\u044c \\u043f\\u043e\\u0445\\u043e\\u0436\\u0438\\u04