INSERT INTO `polls` (`id`, `title`, `content`, `active`, `created_at`, `updated_at`) VALUES
(2, 'Виды и типы ручек', '<p>Несмотря на всеобщую компьютеризацию, переход на электронный документооборот, развитие интернет-сервисов для обмена письменными сообщениями и достижения техники в сфере фиксации информации, старая добрая ручка не сдает своих позиций. Представить себе жизнь и работу многих людей без этого простого и распространенного пишущего инструмента невозможно. Как бы быстро ни развивались технологии, от &laquo;аналогового&raquo; письма человечество не откажется еще очень долго. А вместе с ним будет потребность и в ручках.</p>', 1, '2017-05-24 04:21:11', '2017-05-25 07:31:48');
